'use strict';

var define = require('define-properties');
var callBind = require('call-bind');

var implementation = require('array.prototype.tosorted/implementation');
var getPolyfill = require('array.prototype.tosorted/polyfill');
var polyfill = getPolyfill();
var shim = require('array.prototype.tosorted/shim');

var bound = callBind(polyfill);

define(bound, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = bound;
