'use strict';

var callBind = require('call-bind');
var define = require('define-properties');

var implementation = require('string.prototype.matchall/implementation');
var getPolyfill = require('string.prototype.matchall/polyfill');
var shim = require('string.prototype.matchall/shim');

var boundMatchAll = callBind(implementation);

define(boundMatchAll, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = boundMatchAll;
